import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import { TweenMax } from 'gsap';
import cx from 'classnames';

import ShapeOverlays from './ShapeOverlays';

class Nav extends Component {
  state = {
    isOpen: false,
    isAnimating: false,
  };

  toggleMenu = () => {
    if (!this.state.isAnimating) {
      this.setState(
        {
          isOpen: !this.state.isOpen,
          isAnimating: true,
        },
        () => {
          if (this.state.isOpen) {
            document.body.classList.add('menu-is-open');
            setTimeout(() => {
              TweenMax.staggerTo(
                '.nav__menuLink',
                0.25,
                {
                  opacity: 1,
                  y: 0,
                },
                0.05
              );
            }, 200);
          } else {
            document.body.classList.remove('menu-is-open');
            TweenMax.staggerTo(
              '.nav__menuLink',
              0.25,
              {
                opacity: 0,
                y: '-=10',
              },
              0.05
            );
          }

          setTimeout(() => {
            this.setState({
              isAnimating: false,
            });
          }, 400);
        }
      );
    }
  };

  closeMenu = () => {
    this.setState(
      {
        isOpen: false,
      },
      () => {
        document.body.classList.remove('menu-is-open');
        this.props.removeBackground();

        TweenMax.staggerTo(
          '.nav__menuLink',
          0.25,
          {
            opacity: 0,
            y: '-=15',
          },
          0.05
        );
      }
    );
  };

  componentDidUpdate(prevProps) {
    if (prevProps.location !== this.props.location) {
      document.body.classList.remove('menu-is-open');
      setTimeout(() => {
        this.closeMenu();
      }, 200);
    }
  }

  render() {
    const containerClasses = cx('nav__container', {
        'is-open': this.state.isOpen,
      }),
      menuClasses = cx('nav__menu', {
        'is-open': this.state.isOpen,
      });

    const location = this.props.location.replace(/\//g, '');

    return [
      <ShapeOverlays
        key="Background"
        isAnimatedIn={this.state.isOpen}
        duration={450}
      />,
      <div
        key="Navbar"
        className="nav">
        <div className={containerClasses}>
          <Link
            to="/"
            className="nav__link nav__home">
            <svg
              id="logo"
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 100 100">
              <g id="OVERLAP_3_">
                <path
                  id="XMLID_539_"
                  className="st0"
                  d="M0-.1h100V7H0z"
                />
                <path
                  id="XMLID_538_"
                  className="st0"
                  d="M13.3 13.1H100v7.1H13.3z"
                />
                <path
                  id="XMLID_537_"
                  className="st0"
                  d="M0 79.4h86.7v7.1H0z"
                />
                <path
                  id="XMLID_536_"
                  className="st0"
                  d="M0 92.6h100v7.1H0z"
                />
                <path
                  id="XMLID_535_"
                  className="st0"
                  d="M13.3 23.3V13.1h7.2v10.2"
                />
                <path
                  id="XMLID_534_"
                  className="st0"
                  d="M7.2 49.9v23.4H0V49.9"
                />
                <path
                  id="XMLID_533_"
                  className="st0"
                  d="M0 23.3V-.1h7.2v23.4"
                />
                <path
                  id="XMLID_532_"
                  className="st0"
                  d="M20.5 49.9V60h-7.2V49.9"
                />
                <path
                  id="XMLID_531_"
                  className="st0"
                  d="M100 76.5v23.4h-7.1V76.5"
                />
                <path
                  id="XMLID_530_"
                  className="st0"
                  d="M92.9 49.7V26.4h7.1v23.3"
                />
                <path
                  id="XMLID_529_"
                  className="st0"
                  d="M86.8 76.5v10h-7.2v-10"
                />
                <path
                  id="XMLID_528_"
                  className="st0"
                  d="M79.6 49.7V39.6h7.2v10.1"
                />
                <g id="_x34__3_">
                  <path
                    id="XMLID_19_"
                    className="st0"
                    d="M0 66.1h100v7.1H0z"
                  />
                </g>
                <g id="_x33__3_">
                  <path
                    id="XMLID_17_"
                    className="st0"
                    d="M13.3 52.9H100V60H13.3z"
                  />
                </g>
                <g id="_x32__3_">
                  <path
                    id="XMLID_15_"
                    className="st0"
                    d="M0 39.6h86.7v7.1H0z"
                  />
                </g>
                <g id="_x31__3_">
                  <path
                    id="XMLID_13_"
                    className="st0"
                    d="M0 26.4h100v7.1H0z"
                  />
                </g>
              </g>
            </svg>
          </Link>

          <div
            className="nav__burgerBox"
            onClick={this.toggleMenu}
            role="button">
            <span>Menu</span>
            <div className="nav__hamburger">
              <span></span>
              <span></span>
              <span></span>
            </div>
          </div>

          <Link
            to="/about"
            className={`nav__link nav__about nav--${location}`}>
            About
          </Link>
        </div>

        <nav className={menuClasses}>
          <div className="nav__menuContainer">
            <ul className={`nav__items nav--${location}`}>
              <li className="nav__item">
                <Link
                  to="#"
                  className="nav__menuLink">
                  Verizon 5G
                </Link>
              </li>
              <li className="nav__item">
                <Link
                  to="#"
                  className="nav__menuLink">
                  Trinet
                </Link>
              </li>
              <li className="nav__item">
                <Link
                  to="#"
                  className="nav__menuLink">
                  Cartier
                </Link>
              </li>
              <li className="nav__item">
                <Link
                  to="/marquee-sports"
                  className="nav__menuLink">
                  Marquee Sports
                </Link>
              </li>
              <li className="nav__item">
                <Link
                  to="/odyssey"
                  className="nav__menuLink">
                  Odyssey
                </Link>
              </li>
              <li className="nav__item">
                <Link
                  to="/etc"
                  className="nav__menuLink">
                  Everything Else
                </Link>
              </li>
            </ul>

            <p className="nav__menuCta">
              *For more work samples, email me at{' '}
              <a
                href="mailto:nelsonschang@gmail.com"
                className="nav__menuEmail">
                nelsonschang@gmail.com
              </a>
              .
            </p>
          </div>
        </nav>
      </div>,
    ];
  }
}

export default Nav;
